// Updated code to fix issues with add, delete, and update buttons

import React, { useState, useEffect } from 'react';
import API from '../services/api';
import { useNavigate } from 'react-router-dom';

interface Task {
  id: number;
  title: string;
  description: string;
  isComplete: boolean;
}

const Tasks: React.FC = () => {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [editingTaskId, setEditingTaskId] = useState<number | null>(null);
  const [editTitle, setEditTitle] = useState('');
  const [editDescription, setEditDescription] = useState('');
  const navigate = useNavigate();

  const fetchTasks = async () => {
    try {
      const { data } = await API.get('/tasks');
      setTasks(data);
    } catch (err) {
      console.error('Failed to fetch tasks:', err);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const handleAddTask = async () => {
    if (!title || !description) return;
    try {
      const { data } = await API.post('/tasks', { title, description });
      setTasks((prev) => [...prev, data]);
      setTitle('');
      setDescription('');
    } catch (err) {
      console.error('Failed to add task:', err);
    }
  };

  const handleDeleteTask = async (id: number) => {
    try {
      await API.delete(`/tasks/${id}`);
      setTasks((prev) => prev.filter((task) => task.id !== id));
    } catch (err) {
      console.error('Failed to delete task:', err);
    }
  };

  const handleMarkComplete = async (id: number) => {
    try {
      const { data } = await API.put(`/tasks/${id}`, {
        ...tasks.find((task) => task.id === id),
        isComplete: true,
      });
      setTasks((prev) => prev.map((task) => (task.id === id ? data : task)));
    } catch (err) {
      console.error('Failed to mark task complete:', err);
    }
  };

  const handleEditTask = (task: Task) => {
    setEditingTaskId(task.id);
    setEditTitle(task.title);
    setEditDescription(task.description);
  };

  const handleSaveEdit = async (id: number) => {
    try {
      const { data } = await API.put(`/tasks/${id}`, {
        title: editTitle,
        description: editDescription,
        isComplete: tasks.find((task) => task.id === id)?.isComplete,
      });
      setTasks((prev) => prev.map((task) => (task.id === id ? data : task)));
      setEditingTaskId(null);
      setEditTitle('');
      setEditDescription('');
    } catch (err) {
      console.error('Failed to update task:', err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div>
      <h2>Tasks</h2>
      <button onClick={handleLogout}>Logout</button>
      {tasks.length === 0 ? (
        <p>No tasks</p>
      ) : (
        <ul>
          {tasks.map((task) => (
            <li key={task.id}>
              {editingTaskId === task.id ? (
                <>
                  <input value={editTitle} onChange={(e) => setEditTitle(e.target.value)} />
                  <input value={editDescription} onChange={(e) => setEditDescription(e.target.value)} />
                  <button onClick={() => handleSaveEdit(task.id)}>Save</button>
                </>
              ) : (
                <>
                  {task.title} - {task.description} ({task.isComplete ? '✅ Complete' : '❌ Incomplete'})
                  <button onClick={() => handleMarkComplete(task.id)}>Mark Complete</button>
                  <button onClick={() => handleEditTask(task)}>Edit</button>
                  <button onClick={() => handleDeleteTask(task.id)}>Delete</button>
                </>
              )}
            </li>
          ))}
        </ul>
      )}
      <h3>Add Task</h3>
      <input placeholder="Title" value={title} onChange={(e) => setTitle(e.target.value)} />
      <input placeholder="Description" value={description} onChange={(e) => setDescription(e.target.value)} />
      <button onClick={handleAddTask}>Add Task</button>
    </div>
  );
};

export default Tasks;
