// üîß Updated Backend (tasks.ts) with Debug Logs

import { Router, Request, Response, NextFunction } from 'express';
import { RequestHandler } from 'express';
import { pool } from '../db';
import { authenticateToken } from '../middleware/authenticate';

interface AuthenticatedRequest extends Request {
  user?: { id: number; username: string };
}

const router: Router = Router();

// üîç GET tasks for logged-in user
router.get('/', authenticateToken, (async (req, res, next) => {
  console.log('GET /tasks called');
  try {
    const { user } = req as AuthenticatedRequest;
    const userId = user?.id;

    console.log(`User ID extracted: ${userId}`);
    if (!userId) return res.status(401).json({ error: 'Unauthorized' });

    const tasks = await pool.query('SELECT * FROM tasks WHERE "userId" = $1', [userId]);
    console.log('Tasks fetched from DB:', tasks.rows);
    res.json(tasks.rows);
  } catch (err) {
    console.error('‚ùå Error fetching tasks:', err);
    next(err);
  }
}) as RequestHandler);

// ‚úçÔ∏è POST new task for logged-in user
router.post('/', authenticateToken, (async (req, res, next) => {
  console.log('POST /tasks called:', req.body);
  const { title, description } = req.body;
  const { user } = req as AuthenticatedRequest;
  const userId = user?.id;

  if (!userId) return res.status(401).json({ error: 'Unauthorized' });

  try {
    const result = await pool.query(
      'INSERT INTO tasks (title, description, "userId", "isComplete") VALUES ($1, $2, $3, $4) RETURNING *',
      [title, description, userId, false]
    );
    console.log('Task created:', result.rows[0]);
    res.status(201).json(result.rows[0]);
  } catch (err) {
    console.error('‚ùå Error creating task:', err);
    next(err);
  }
}) as RequestHandler);

// üìù PUT (Update) existing task for logged-in user
router.put('/:id', authenticateToken, (async (req, res, next) => {
  console.log('PUT /tasks/:id called:', req.params, req.body);
  const { id } = req.params;
  const { title, description, isComplete } = req.body;
  const { user } = req as AuthenticatedRequest;
  const userId = user?.id;

  if (!userId) return res.status(401).json({ error: 'Unauthorized' });

  try {
    const result = await pool.query(
      'UPDATE tasks SET title = COALESCE($1, title), description = COALESCE($2, description), "isComplete" = COALESCE($3, "isComplete") WHERE id = $4 AND "userId" = $5 RETURNING *',
      [title, description, isComplete, id, userId]
    );
    console.log('Task updated:', result.rows[0]);
    res.json(result.rows[0]);
  } catch (err) {
    console.error('‚ùå Error updating task:', err);
    next(err);
  }
}) as RequestHandler);

// üóë DELETE task for logged-in user
router.delete('/:id', authenticateToken, (async (req, res, next) => {
  console.log('DELETE /tasks/:id called:', req.params);
  const { id } = req.params;
  const { user } = req as AuthenticatedRequest;
  const userId = user?.id;

  if (!userId) return res.status(401).json({ error: 'Unauthorized' });

  try {
    const result = await pool.query(
      'DELETE FROM tasks WHERE id = $1 AND "userId" = $2 RETURNING *',
      [id, userId]
    );
    console.log('Task deleted:', result.rows[0]);
    res.json({ message: 'Task deleted successfully' });
  } catch (err) {
    console.error('‚ùå Error deleting task:', err);
    next(err);
  }
}) as RequestHandler);

export default router;


// üé® Updated Frontend (Tasks.tsx) with Debug Logs

import React, { useState, useEffect } from 'react';
import API from '../services/api';
import { useNavigate } from 'react-router-dom';

interface Task {
  id: number;
  title: string;
  description: string;
  isComplete: boolean;
}

const Tasks: React.FC = () => {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const navigate = useNavigate();

  const fetchTasks = async () => {
    console.log('Fetching tasks...');
    try {
      const { data } = await API.get('/tasks');
      console.log('Tasks fetched:', data);
      setTasks(data);
    } catch (err) {
      console.error('Failed to fetch tasks:', err);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const handleAddTask = async () => {
    console.log('Adding task:', title, description);
    if (!title || !description) return;
    try {
      const { data } = await API.post('/tasks', { title, description });
      console.log('Task added:', data);
      setTasks((prev) => [...prev, data]);
      setTitle('');
      setDescription('');
    } catch (err) {
      console.error('Failed to add task:', err);
    }
  };

  const handleDeleteTask = async (id: number) => {
    console.log('Deleting task with id:', id);
    try {
      await API.delete(`/tasks/${id}`);
      console.log(`Task ${id} deleted successfully`);
      setTasks((prev) => prev.filter((task) => task.id !== id));
    } catch (err) {
      console.error('Failed to delete task:', err);
    }
  };

  const handleLogout = () => {
    console.log('Logging out...');
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div>
      <h2>Tasks</h2>
      <button onClick={handleLogout}>Logout</button>
      {tasks.length === 0 ? (
        <p>No tasks</p>
      ) : (
        <ul>
          {tasks.map((task) => (
            <li key={task.id}>
              {task.title} - {task.description} ({task.isComplete ? '‚úÖ Complete' : '‚ùå Incomplete'})
              <button onClick={() => handleDeleteTask(task.id)}>Delete</button>
            </li>
          ))}
        </ul>
      )}
      <h3>Add Task</h3>
      <input placeholder="Title" value={title} onChange={(e) => setTitle(e.target.value)} />
      <input placeholder="Description" value={description} onChange={(e) => setDescription(e.target.value)} />
      <button onClick={handleAddTask}>Add Task</button>
    </div>
  );
};

export default Tasks;
